import React from 'react'
import { Form, Icon, Button } from 'semantic-ui-react'

function ContractForm({ conprice, conName, conFile, conDate, handleInputChange, handleAddContract }) {

  const convertToDateObject = (dateString) => {
    const parts = dateString.split('/'); // Yıl/Ay/Gün olarak böl
    const year = parseInt(parts[0]);
    const month = parseInt(parts[1]);
    const day = parseInt(parts[2]);

    return new Date(year, month - 1, day); // Ay 0-11 aralığında olduğu için 1 çıkarıyoruz
  };

  const createBtnDisabled = conprice.trim() === '' || conName.trim() === '' || !conDate
  return (
    <Form onSubmit={handleAddContract}>
      <Form.Group>
        <Form.Input
          name='conprice'
          placeholder='Cayma Bedeli'
          value={conprice}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conName'
          placeholder='Şirket İsmi'
          value={conName}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conDate'
          type='date'
          placeholder='Tarih'
          value={conDate}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conFile'
          placeholder='Belge'
          value={conFile}
          onChange={handleInputChange}
        />
        <Button icon labelPosition='right' disabled={createBtnDisabled}>
          Oluştur<Icon name='add' />
        </Button>
      </Form.Group>
    </Form>
  )
}

export default ContractForm
