import React from 'react'
import { Form, Icon, Button } from 'semantic-ui-react'

function ContractForm({ conprice, conName, conFile, conDate, handleInputChange, handleAddContract }) {


  const createBtnDisabled = conprice.trim() === '' || conName.trim() === '' 
  return (
    <Form onSubmit={handleAddContract}>
      <Form.Group>
        <Form.Input
          name='conprice'
          placeholder='Cayma Bedeli'
          value={conprice}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conName'
          placeholder='Şirket İsmi'
          value={conName}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conDate'
          type='text'  // input type'ını text olarak ayarla
          placeholder='Yıl/Ay/Gün'
          value={conDate}
          onChange={handleInputChange}
        />
        <Form.Input
          name='conFile'
          placeholder='Belge'
          value={conFile}
          onChange={handleInputChange}
        />
        <Button icon labelPosition='right' disabled={createBtnDisabled} onClick={() => {
          // Tarih alanındaki değeri çevir ve handleInputChange fonksiyonu ile state'i güncelle
          handleInputChange('conDate', convertToDateObject(conDate));
          handleAddContract();
        }}>
          Oluştur<Icon name='add' />
        </Button>
      </Form.Group>
    </Form>
  )
}

export default ContractForm
